import React from "react";
import EditIcon from "@material-ui/icons/Edit";
import CheckIcon from "@material-ui/icons/Check";
import PhotoCameraIcon from '@material-ui/icons/PhotoCamera';  
import { makeStyles } from "@material-ui/core/styles";        
import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";      
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import "./Flashcard.css";
import { Box, TextField } from "@material-ui/core";
import ImageUploading, { ImageListType } from "react-images-uploading";
import DeleteIcon from '@material-ui/icons/Delete';
import ClickAwayListener from '@material-ui/core/ClickAwayListener'

const useStyles = makeStyles({
  root: {
    transition: "0.6s",
    position: 'relative',
    width: 300,
    height: 'fitContent',
    marginTop: 30,
  },
  media: {
    height: 140,
  },
  input: {
    width: '100%',
    marginBottom: 15,
  },
  editMode: {
    height: 'fitContent',
    width: 445,
    transition: '0.6s',

  },
  editIcon: {
    zIndex: 2,
    postion: 'relative',
    top: -12,
    left: 12,
  },
  description: {
    fontSize: 12,
    height: 'fitContent',

  },
  actions: {
    marginLeft: 'auto',
  },
  uploadOption: {
    display: 'none'
  }
});

interface IFlashcardProps {
  id?: number;
  desc?: string;
  title?: string;
}

const Flashcard: React.FC<IFlashcardProps> = ({}) => {
  const classes = useStyles();
  //card edit states
  const [editMode, setEditMode] = React.useState(false);
  const [hide, setHide] = React.useState(false);

  //card content states
  const [images, setImages] = React.useState([]);
  const [title, setTitle] = React.useState("default title");
  const [desc, setDesc] = React.useState("default description");

  const onChange = (
    imageList: ImageListType,
    addUpdateIndex: number[] | undefined
  ) => {
    // data for submit
    console.log(imageList, addUpdateIndex);
    setImages(imageList as never[]);
  };

  const handleClick = () => {
    setHide((prev) => !prev);
  };

  return (
    <ClickAwayListener onClickAway={()=>setEditMode(prev => false)}>
      <div>
      <div className={classes.uploadOption}>
      <ImageUploading multiple value={images} onChange={onChange} maxNumber={1}>
        {({
          imageList,
          onImageUpload,
          onImageRemoveAll,
          onImageUpdate,
          onImageRemove,
          isDragging,
          dragProps,
        }) => (
          // write your building UI
          <div className="upload__image-wrapper">
            <button
              style={isDragging ? { color: "red" } : undefined}
              onClick={onImageUpload}
              {...dragProps}
            >
              Click or Drop here
            </button>
            &nbsp;
            <button onClick={onImageRemoveAll}>Remove all images</button>
            {imageList.map((image, index) => (
              <div key={index} className="image-item">
                <img src={image.dataURL} alt="" width="100" />
                <div className="image-item__btn-wrapper">
                  <button onClick={() => onImageUpdate(index)}>Update</button>
                  <button onClick={() => onImageRemove(index)}>Remove</button>
                </div>
              </div>
            ))}
          </div>
        )}
          </ImageUploading>
          </div>

      <Card className={`${ !editMode ? classes.root : classes.editMode}`}>
        
      
          <CardMedia
            className={`media ${hide && "hide"}`}
            image="data:image/png;base64,"
            title={title}
          />
      
          <CardContent className={`${hide ? " hide classes.description" : classes.description}`}>
            {editMode ? (
              <TextField
                className={classes.input}
                required
                value={title}
                onChange={(event: React.ChangeEvent<HTMLInputElement>) =>
                  setTitle(event.target.value)
                }
                id="title"
                label="TITLE"
                variant="outlined"
              />
            ) : (
              <Typography gutterBottom variant="h5" component="h2">
                {title}
              </Typography>
            )}

            {!editMode ? (
              <Typography variant="body2" color="textSecondary" component="p">
                {desc}
              </Typography>
            ) : (
              <TextField
                className={classes.input}
                value={desc}
                onChange={(event: React.ChangeEvent<HTMLInputElement>) =>
                  setDesc(event.target.value)
                }
                required
                id="description"
                label="DESCRIPTION"
                variant="outlined"
              />
            )}
          </CardContent>
        <CardActions  className={classes.actions}>
            {!hide ? (
                <Button onClick={handleClick} size="small" color="primary">Hide</Button>
            ) : (
                <Button onClick={handleClick} size="small" color="primary">Unhide</Button>
            )}
          
            {/* Edit button & Confirm edit button */}
            {editMode ? (<Button><CheckIcon className={classes.editIcon} onClick={() => setEditMode((prev) => !prev)} /></Button>) : (<Button ><EditIcon fontSize='small' color={'primary'} onClick={() => setEditMode((prev) => !prev)}/></Button>)}
          <Button>
            <DeleteIcon fontSize='small' color={'secondary'} />
            </Button>
        </CardActions>
      </Card>
      </div>
      </ClickAwayListener>
  );
};

export { Flashcard };